import React, { useEffect, useState, useContext } from "react";
import { createBrowserHistory } from "history";
import { categories, counties } from "../../utils/data";
import { useForm } from "react-hook-form";
import _ from "lodash";
import { Link } from "react-router-dom";
import { GlobalContext } from "../../context/GlobalState";
import $ from "jquery";
import moment from "moment";

const SearchBox = () => {
  const { searchPhotographer, isLoading } = useContext(GlobalContext);

  const history = createBrowserHistory();

  const [category, setCategory] = useState(null);
  const [location, setLocation] = useState(null);
  const [booked_on, setBookedOn] = useState(null);

  const updateLocalStorageItem = (name, value) => {
    if (!localStorage.hasOwnProperty("_search")) {
      let search = {
        category: null,
        location: null,
        booked: null,
      };

      localStorage._search = JSON.stringify(search);
    }

    const search = JSON.parse(localStorage._search);

    const updated_search = { ...search, [name]: value };

    // const name = name;

    // localStorage._search = JSON.stringify(updated_search);

    console.log(search);

    // localStorage._search = JSON.stringify(value);

    // const search = JSON.parse(localStorage._search);

    // const updated_search = { ...search, category: value };

    // setSearch(JSON.parse(localStorage._search));
  };

  const updateCategory = (value) => {
    localStorage._search = JSON.stringify(value);

    // const search = JSON.parse(localStorage._search);

    // const updated_search = { ...search, category: value };

    // setSearch(JSON.parse(localStorage._search));
  };

  const updateLocation = (value) => {
    console.log(value);
    // const search = JSON.parse(localStorage._search);

    // const updated_search = { ...search, location: value };

    // localStorage._search = JSON.stringify(updated_search);

    // setSearch(JSON.parse(localStorage._search));
  };

  const updateDate = (value) => {
    console.log(value);
    // const search = JSON.parse(localStorage._search);

    // const updated_search = { ...search, booked_on: value };

    // localStorage._search = JSON.stringify(updated_search);

    // setSearch(JSON.parse(localStorage._search));
  };

  const { register, handleSubmit, errors, reset } = useForm();

  const onSubmit = (data) => {
    console.log(data);

    searchPhotographer(data);
  };

  useEffect(() => {
    $("#dateplaceholder").change(function (evt) {
      var date = new Date($("#dateplaceholder").val());

      $("#dateplaceholder").attr("type", "text");

      $("#dateplaceholder").val(
        date.getDate() + "-" + (date.getMonth() + 1) + "-" + date.getFullYear()
      );
    });

    $("#dateplaceholder").focus(function (evt) {
      $("#dateplaceholder").attr("type", "date");
      setTimeout('$("#dateplaceholder").click();', 500);
    });

    $("#dateplaceholder").attr("type", "text");

    // if (
    //   localStorage.hasOwnProperty("_search") &&
    //   !_.isEmpty(JSON.parse(localStorage._search))
    // ) {
    //   setSearch(JSON.parse(localStorage._search));
    // }

    return () => {
      // cleanup;
    };
  }, []);

  return (
    <>
      <div className="row">
        <div className="col-md-12">
          <form id="login-form" onSubmit={handleSubmit(onSubmit)}>
            <div className="utf-intro-banner-search-form-block margin-top-50">
              <div className="utf-intro-search-field-item">
                <select
                  className="selectpicker default"
                  data-live-search="true"
                  data-selected-text-format="count"
                  data-size="5"
                  title={category ? category : "Select Category"}
                  name="category"
                  ref={register()}
                  onChange={(e) =>
                    updateLocalStorageItem(e.target.name, e.target.value)
                  }
                >
                  {category != null && (
                    <option value={category}>{category}</option>
                  )}
                  {categories.map((c, i) => (
                    <option key={i} value={c.id}>
                      {c.name}&nbsp;photographer
                    </option>
                  ))}
                </select>
              </div>
              <div className="utf-intro-search-field-item">
                <select
                  className="selectpicker default"
                  data-live-search="true"
                  data-selected-text-format="count"
                  data-size="5"
                  title={location ? location : "Select Location"}
                  name="location"
                  ref={register()}
                  onChange={(e) =>
                    updateLocalStorageItem(e.target.name, e.target.value)
                  }
                >
                  {counties.map((c, i) => (
                    <option key={i} value={i + 1}>
                      {c.name} County
                    </option>
                  ))}
                </select>
              </div>
              <div className="utf-intro-search-field-item">
                <input
                  type="date"
                  className="selectpicker default"
                  ref={register()}
                  min={moment().add(-1).format("YYYY-MM-DD")}
                  id="dateplaceholder"
                  name="booked_on"
                  placeholder={booked_on ? booked_on : "Book them on?"}
                  onChange={(e) =>
                    updateLocalStorageItem(e.target.name, e.target.value)
                  }
                />
              </div>
              <div className="utf-intro-search-button">
                <button className="button ripple-effect" type="submit">
                  <i className="icon-material-outline-search"></i>Search
                </button>
              </div>
            </div>
          </form>

          <p className="utf-trending-silver-item">
            <span className="utf-trending-black-item">
              Trending Keywords :&nbsp;
            </span>
            {categories.slice(0, 5).map((c, i) => (
              <Link to={`/category/${c.slug}`}>
                {c.name}
                {history.location.pathname !== "/" && <>,&nbsp;</>}
              </Link>
            ))}
          </p>
        </div>
      </div>
    </>
  );
};

export default SearchBox;
