import React, { useState, useEffect, useContext } from "react";
import { Link } from "react-router-dom";
import { GlobalContext } from "../../context/GlobalState";
import $ from "jquery";

const Login = () => {
  const { userLogin, isLoading, user_info } = useContext(GlobalContext);

  const initialValues = {
    email: "",
    password: "",
  };

  const [formValues, setFormValues] = useState(initialValues);

  const handleChange = (e) => {
    const { value, name } = e.target;

    console.log(e.target);

    // setState({
    //   ...state,
    //   [name]: value,
    // });
  };

  useEffect(() => {
    // $("form").submit(function (e) {
    //   e.preventDefault();

    //   // onSubmit();
    // });

    // wait until DOM is loaded
    $(document).ready(function () {
      form.addEventListener("submit", function (e) {
        e.preventDefault();
        var data = new FormData(form);
        for (const [name, value] of data) {
          console.log(name, value);
        }
      });
    });

    return () => {
      //   cleanup
    }; // eslint-disable-next-line
  }, [isLoading]);

  return (
    <form>
      <div class="utf-no-border">
        <input
          type="text"
          class="utf-with-border"
          name="email"
          placeholder="Email Address"
          value={formValues.email}
          onChange={(e) => {
            console.log("changed");
            handleChange(e);
          }}
        />
      </div>
      <div class="utf-no-border">
        <input
          type="password"
          class="utf-with-border"
          name="password"
          placeholder="Password"
          value={formValues.password}
          onChange={(e) => handleChange(e)}
        />
      </div>
      <div class="checkbox margin-top-0">
        <input type="checkbox" id="two-step" />
        <label for="two-step">
          <span class="checkbox-icon"></span> Remember Me
        </label>
      </div>
      <Link to="#" class="forgot-password">
        Forgot Password?
      </Link>
      <button
        class="button full-width utf-button-sliding-icon ripple-effect"
        type="submit"
      >
        Log In <i class="icon-feather-chevron-right"></i>
      </button>
    </form>
  );
};

export default Login;
