import React, { useEffect, useContext } from "react";
import { Link, Redirect } from "react-router-dom";
import { useForm } from "react-hook-form";
import { GlobalContext } from "../../context/GlobalState";
import Footer from "../../components/layout/Footer";

const VerifyCode = () => {
  const { userRegistration, isLoading, isLoggedIn } = useContext(GlobalContext);

  const { register, handleSubmit, errors, reset } = useForm();

  const onSubmit = (data) => {
    userRegistration(data);

    reset();
  };

  if (isLoggedIn) {
    let to = "/";

    return <Redirect to={to} />;
  } else {
    return (
      <>
        <div className="login-signup-form">
          <div className="container">
            <div className="row">
              <div className="col-xl-12">
                <div className="form-box">
                  <div className="utf-welcome-text-item">
                    <h3>Verify code</h3>
                  </div>
                  <form onSubmit={handleSubmit(onSubmit)}>
                    <div className="utf-no-border">
                      <span className="err_msg">
                        {errors.username && errors.username.message}
                      </span>
                      <input
                        type="text"
                        className="utf-with-border"
                        name="code"
                        placeholder="Enter code"
                        ref={register({
                          required: "Please enter code",
                          pattern: {
                            value: /^[^\s]+(?:$|.*[^\s]+$)/,
                            message: "No spaces",
                          },
                        })}
                      />
                    </div>
                    <button
                      className="margin-top-10 button full-width utf-button-sliding-icon ripple-effect"
                      type="submit"
                    >
                      Verify <i className="icon-feather-chevron-right"></i>
                    </button>
                  </form>
                </div>
              </div>
            </div>
            <div className="row">
              <div className="col-xl-4 col-12"></div>
            </div>
          </div>
        </div>
        <Footer />
      </>
    );
  }
};

export default VerifyCode;
