import React from "react";
import { Link } from "react-router-dom";
import Loader from "../../components/Loader";
import { useForm } from "react-hook-form";

const Login = () => {
  const { register, handleSubmit, errors, reset } = useForm();

  const onSubmit = (data) => {
    console.log(data);

    reset();
  };

  return (
    <>
      <Loader />
      <div className="login-signup-form">
        <div className="container">
          <div className="row">
            <div className="col-xl-12">
              <div className="form-box">
                <div className="utf-welcome-text-item">
                  <h3>Sign in to Continue</h3>
                  <span>
                    Don't Have an Account?{" "}
                    <Link href="#" className="register-tab">
                      Sign Up!
                    </Link>
                  </span>
                </div>
                <form onSubmit={handleSubmit(onSubmit)}>
                  <div className="utf-no-border">
                    <input
                      type="text"
                      className="utf-with-border"
                      name="email"
                      placeholder="Email Address"
                      ref={register({
                        required: "Enter grade",
                      })}
                    />
                    <span className="err_msg">
                      {errors.grade && errors.grade.message}
                    </span>
                  </div>
                  <div className="utf-no-border">
                    <input
                      type="password"
                      className="utf-with-border"
                      name="password"
                      placeholder="Password"
                      ref={register({
                        required: "Enter grade",
                      })}
                    />
                    <span className="err_msg">
                      {errors.grade && errors.grade.message}
                    </span>
                  </div>
                  <div class="checkbox margin-top-0">
                    <input type="checkbox" id="two-step0" />
                    <label for="two-step0">
                      <span class="checkbox-icon"></span> I Have Read and Agree
                      to the <Link to="/">Terms &amp; Conditions</Link>
                    </label>
                  </div>
                  <br />
                  <button
                    className="button full-width utf-button-sliding-icon ripple-effect"
                    type="submit"
                  >
                    Log In <i className="icon-feather-chevron-right"></i>
                  </button>
                </form>
              </div>
            </div>
          </div>
          <div className="row">
            <div className="col-xl-4 col-12"></div>
          </div>
        </div>
      </div>
    </>
  );
};

export default Login;
