import React, { useEffect, useContext } from "react";
// import { Link } from "react-router-dom";
import { useForm } from "react-hook-form";
import { GlobalContext } from "../../context/GlobalState";

const Login = () => {
  const { userLogin, isLoading, user_info } = useContext(GlobalContext);

  const { register, handleSubmit } = useForm();
  const onError = (errors, e) => console.log(errors, e);

  const onSubmit = (data, e) => console.log(data, e);

  // const onSubmit = (data) => {
  //   console.log(data);
  //   userLogin(data);
  // };

  useEffect(() => {
    if (isLoading) {
      reset();
    }
    return () => {
      //   cleanup
    };
  }, [isLoading]);

  return (
    <>
      <form id="login-form" onSubmit={handleSubmit(onSubmit)}>
        <div className="utf-no-border">
          <span className="err_msg">
            {/* {errors.email && errors.email.message} */}
          </span>
          <input
            type="text"
            className="utf-with-border"
            placeholder="Email Address"
            {...register("email")}
            // ref={register({
            //   required: "Email cannot be empty",
            //   pattern: {
            //     value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
            //     message: "Enter a valid email address",
            //   },
            // })}
          />
        </div>
        <div className="utf-no-border">
          <span className="err_msg">
            {/* {errors.password && errors.password.message} */}
          </span>
          <input
            type="password"
            className="utf-with-border"
            name="password"
            placeholder="Password"
            ref={register({
              required: "Password cannot be empty",
            })}
          />
        </div>
        <div className="checkbox margin-top-0">
          <input type="checkbox" id="two-step" />
          <label htmlFor="two-step">
            <span className="checkbox-icon"></span> Remember Me
          </label>
        </div>
        <a href="#" className="forgot-password">
          Forgot Password?
        </a>
        <button
          className="button full-width utf-button-sliding-icon ripple-effect"
          type="submit"
        >
          Log In <i className="icon-feather-chevron-right"></i>
        </button>
      </form>
    </>
  );
};

export default Login;
