import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { GlobalContext } from "../../context/GlobalState";
import { useForm } from "react-hook-form";

const LoginSignupupPopup = () => {
  const { user_info } = useContext(GlobalContext);

  const { register, handleSubmit, errors, reset } = useForm();

  const onSubmit = (data) => {
    console.log("ngamia one");
    // console.log(data);
    // userLogin(data);
  };

  const registerUser = () => {
    document.user_form.action = "register.html";
    alert(document.user_form.action);
    return false;
  };

  return (
    <>
      <div
        id="utf-signin-dialog-block"
        class="zoom-anim-dialog mfp-hide dialog-with-tabs"
      >
        <div class="utf-signin-form-part">
          <ul class="utf-popup-tabs-nav-item">
            <li>
              <a href="#login">Log In</a>
            </li>
            <li>
              <a href="#register">Register</a>
            </li>
          </ul>
          <div class="utf-popup-container-part-tabs">
            <div class="utf-popup-tab-content-item" id="login">
              <div class="utf-welcome-text-item">
                <h3>Welcome Back Sign in to Continue</h3>
                <span>
                  Don't Have an Account?{" "}
                  <Link to="#" class="register-tab">
                    Sign Up!
                  </Link>
                </span>
              </div>
              <form name="user_form">
                <div class="utf-no-border">
                  <input
                    type="text"
                    class="utf-with-border"
                    name="emailaddress"
                    id="emailaddress"
                    placeholder="Email Address"
                    required
                  />
                </div>
                <div class="utf-no-border">
                  <input
                    type="password"
                    class="utf-with-border"
                    name="password"
                    id="password"
                    placeholder="Password"
                    required
                  />
                </div>
                <div class="checkbox margin-top-0">
                  <input type="checkbox" id="two-step" />
                  <label for="two-step">
                    <span class="checkbox-icon"></span> Remember Me
                  </label>
                </div>
                <Link to="#" class="forgot-password">
                  Forgot Password?
                </Link>
                {/* 
                <input
                  class="button full-width utf-button-sliding-icon ripple-effect"
                  type="submit"
                  value="Register"
                  onclick="return registerUser()"
                ></input> */}
                <button
                  class="button full-width utf-button-sliding-icon ripple-effect"
                  type="submit"
                  onclick="return registerUser()"
                  // form="login-form"
                >
                  Log In <i class="icon-feather-chevron-right"></i>
                </button>
              </form>
            </div>
            <div class="utf-popup-tab-content-item" id="register">
              <div class="utf-welcome-text-item">
                <h3>Create your Account!</h3>
                <span>
                  Don't Have an Account?&nbsp;
                  <Link to="#" class="login-tab">
                    Log in
                  </Link>
                </span>
              </div>
              {/* <form method="post" id="utf-register-account-form"> */}
              <form id="utf-register-account-form">
                <div class="utf-no-border">
                  <input
                    type="text"
                    class="utf-with-border"
                    name="name"
                    id="name"
                    placeholder="User Name"
                    required
                  />
                </div>
                <div class="utf-no-border">
                  <input
                    type="text"
                    class="utf-with-border"
                    name="emailaddress-register"
                    id="emailaddress-register"
                    placeholder="Email Address"
                    required
                  />
                </div>
                <div class="utf-no-border margin-bottom-20">
                  <select
                    class="selectpicker utf-with-border"
                    data-size="5"
                    title="Select Category"
                  >
                    <option>User</option>
                    <option>Photographer</option>
                  </select>
                </div>
                <div
                  class="utf-no-border"
                  title="Should be at least 8 characters long"
                  data-tippy-placement="bottom"
                >
                  <input
                    type="password"
                    class="utf-with-border"
                    name="password-register"
                    id="password-register"
                    placeholder="Password"
                    required
                  />
                </div>
                <div class="utf-no-border">
                  <input
                    type="password"
                    class="utf-with-border"
                    name="password-repeat-register"
                    id="password-repeat-register"
                    placeholder="Repeat Password"
                    required
                  />
                </div>
                <div class="checkbox margin-top-0">
                  <input type="checkbox" id="two-step0" />
                  <label for="two-step0">
                    <span class="checkbox-icon"></span> I Have Read and Agree to
                    the{" "}
                    <Link to="#" class="register-tab">
                      Terms &amp; Conditions
                    </Link>
                  </label>
                </div>
              </form>
              <button
                class="margin-top-10 button full-width utf-button-sliding-icon ripple-effect"
                type="submit"
                form="utf-register-account-form"
              >
                Register <i class="icon-feather-chevron-right"></i>
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
    // <>
    //   <div
    //     id="utf-signin-dialog-block"
    //     class="zoom-anim-dialog mfp-hide dialog-with-tabs"
    //   >
    //     <div class="utf-signin-form-part">
    //       <ul class="utf-popup-tabs-nav-item">
    //         <li>
    //           <a href="#login">Log In</a>
    //         </li>
    //         <li>
    //           <a href="#register">Register</a>
    //         </li>
    //       </ul>
    //       <div class="utf-popup-container-part-tabs">
    //         <div className="utf-popup-tab-content-item" id="login">
    //           <div className="utf-welcome-text-item">
    //             <h3>
    //               Welcome Back Sign in to Continue{" "}
    //               {user_info && user_info.username}
    //             </h3>
    //             <span>
    //               Don't Have an Account?&nbsp;
    //               <a href="#register" className="register-tab">
    //                 Sign Up!
    //               </a>
    //             </span>
    //           </div>
    //           <Login />
    //         </div>
    //         <div className="utf-popup-tab-content-item" id="register">
    //           <div className="utf-welcome-text-item">
    //             <h3>Create your Account!</h3>
    //             <span>
    //               Already have an Account?&nbsp;
    //               <a href="#login" className="login-tab">
    //                 Log In!
    //               </a>
    //             </span>
    //           </div>
    //           <Register />
    //         </div>
    //       </div>
    //     </div>
    //   </div>
    // </>
  );
};

export default LoginSignupupPopup;
